import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { PageHeader, Card, Button, LoadingSpinner } from '../ui';

type DashboardMetrics = {
  assessmentsConducted: number;
  facilitiesAssigned: number;
  campaignsAssigned: number;
};

const DashboardPage: React.FC = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [metrics, setMetrics] = useState<DashboardMetrics>({
    assessmentsConducted: 0,
    facilitiesAssigned: 0,
    campaignsAssigned: 0,
  });

  useEffect(() => {
    // TODO: Fetch metrics from API
    const fetchMetrics = async () => {
      setLoading(true);
      // Simulated API call
      setTimeout(() => {
        setMetrics({
          assessmentsConducted: 45,
          facilitiesAssigned: 3,
          campaignsAssigned: 2,
        });
        setLoading(false);
      }, 500);
    };
    void fetchMetrics();
  }, []);

  if (loading) {
    return (
      <div className="container-fluid py-4">
        <LoadingSpinner />
      </div>
    );
  }

  return (
    <div className="container-fluid py-4">
      <PageHeader
        title="Test Administrator Dashboard"
        subtitle="Overview of your assessment activity"
      />

      <div className="row g-4 mb-4">
        <div className="col-md-4">
          <Card className="text-center">
            <div className="cwpm-score" style={{ fontSize: '3rem' }}>
              {metrics.assessmentsConducted}
            </div>
            <div className="text-secondary mt-2">Assessments Conducted</div>
          </Card>
        </div>
        <div className="col-md-4">
          <Card className="text-center">
            <div className="cwpm-score" style={{ fontSize: '3rem' }}>
              {metrics.facilitiesAssigned}
            </div>
            <div className="text-secondary mt-2">Facilities Assigned</div>
          </Card>
        </div>
        <div className="col-md-4">
          <Card className="text-center">
            <div className="cwpm-score" style={{ fontSize: '3rem' }}>
              {metrics.campaignsAssigned}
            </div>
            <div className="text-secondary mt-2">Campaigns Assigned</div>
          </Card>
        </div>
      </div>

      <Card className="text-center py-5">
        <h3 className="mb-4">Ready to start a new assessment?</h3>
        <Button
          size="lg"
          icon={<i className="fas fa-play me-2"></i>}
          onClick={() => navigate('/assessment/start')}
        >
          Start New Test
        </Button>
      </Card>

      <div className="mt-4 text-center">
        <Button
          variant="secondary"
          icon={<i className="fas fa-history me-2"></i>}
          onClick={() => navigate('/submissions')}
        >
          View Past Submissions
        </Button>
      </div>
    </div>
  );
};

export default DashboardPage;
