import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { Form } from 'react-bootstrap';
import { PageHeader, Card, Button, LoadingSpinner, Modal } from '../ui';

type Student = {
  id: string;
  name: string;
  facilityId: string;
  gradeId: string;
};

type StudentOnboardingPageProps = {
  campaignId?: string;
  facilityId?: string;
  gradeId?: string;
};

const StudentOnboardingPage: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const state = location.state as StudentOnboardingPageProps | null;

  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [students, setStudents] = useState<Student[]>([]);
  const [filteredStudents, setFilteredStudents] = useState<Student[]>([]);
  const [showEnrollmentModal, setShowEnrollmentModal] = useState(false);
  const [enrollmentData, setEnrollmentData] = useState({
    name: '',
    studentId: '',
    dateOfBirth: '',
  });

  useEffect(() => {
    // TODO: Fetch students filtered by facility and grade
    const fetchStudents = async () => {
      setLoading(true);
      // Simulated API call
      setTimeout(() => {
        const mockStudents: Student[] = [
          { id: '1', name: 'John Doe', facilityId: state?.facilityId || '1', gradeId: state?.gradeId || '1' },
          { id: '2', name: 'Jane Smith', facilityId: state?.facilityId || '1', gradeId: state?.gradeId || '1' },
          { id: '3', name: 'Bob Johnson', facilityId: state?.facilityId || '1', gradeId: state?.gradeId || '1' },
        ];
        setStudents(mockStudents);
        setFilteredStudents(mockStudents);
        setLoading(false);
      }, 500);
    };
    void fetchStudents();
  }, [state]);

  useEffect(() => {
    if (searchTerm) {
      const filtered = students.filter((student) =>
        student.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        student.id.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredStudents(filtered);
    } else {
      setFilteredStudents(students);
    }
  }, [searchTerm, students]);

  const handleEnrollmentSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    // TODO: Create new student via API
    const newStudent: Student = {
      id: enrollmentData.studentId,
      name: enrollmentData.name,
      facilityId: state?.facilityId || '',
      gradeId: state?.gradeId || '',
    };
    setStudents([...students, newStudent]);
    setShowEnrollmentModal(false);
    setEnrollmentData({ name: '', studentId: '', dateOfBirth: '' });
    // Select the newly created student
    handleStudentSelect(newStudent.id);
  };

  const handleStudentSelect = (studentId: string) => {
    navigate('/assessment/orf', {
      state: {
        ...state,
        studentId,
      },
    });
  };

  if (loading) {
    return (
      <div className="container-fluid py-4">
        <LoadingSpinner />
      </div>
    );
  }

  return (
    <div className="container-fluid py-4">
      <PageHeader
        title="Student Onboarding"
        subtitle="Search for an existing student or create a new profile"
      />

      <div className="row justify-content-center">
        <div className="col-md-8 col-lg-6">
          <Card>
            <Form.Group className="mb-4">
              <Form.Label className="text-dark fw-bold">Search Student</Form.Label>
              <Form.Control
                type="text"
                placeholder="Search by name or student ID..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </Form.Group>

            {filteredStudents.length > 0 ? (
              <div className="mb-3">
                <h6 className="text-dark mb-3">Select Student:</h6>
                <div className="list-group">
                  {filteredStudents.map((student) => (
                    <button
                      key={student.id}
                      type="button"
                      className="list-group-item list-group-item-action student-card"
                      onClick={() => handleStudentSelect(student.id)}
                    >
                      <div className="d-flex justify-content-between align-items-center">
                        <div>
                          <h6 className="mb-1 text-dark">{student.name}</h6>
                          <small className="text-secondary">ID: {student.id}</small>
                        </div>
                        <i className="fas fa-chevron-right text-secondary"></i>
                      </div>
                    </button>
                  ))}
                </div>
              </div>
            ) : (
              <div className="text-center py-4 text-secondary">
                <p>No students found. Create a new student profile.</p>
              </div>
            )}

            <div className="text-center">
              <Button
                variant="primary"
                icon={<i className="fas fa-plus"></i>}
                onClick={() => setShowEnrollmentModal(true)}
              >
                Create New Student
              </Button>
            </div>
          </Card>
        </div>
      </div>

      <Modal
        show={showEnrollmentModal}
        onHide={() => setShowEnrollmentModal(false)}
        title="Enroll New Student"
        footer={
          <>
            <Button variant="secondary" onClick={() => setShowEnrollmentModal(false)}>
              Cancel
            </Button>
            <Button
              variant="primary"
              type="submit"
              form="enrollmentForm"
            >
              Create Student
            </Button>
          </>
        }
      >
        <Form id="enrollmentForm" onSubmit={handleEnrollmentSubmit}>
          <Form.Group className="mb-3">
            <Form.Label className="text-dark">Student Name</Form.Label>
            <Form.Control
              type="text"
              value={enrollmentData.name}
              onChange={(e) =>
                setEnrollmentData({ ...enrollmentData, name: e.target.value })
              }
              required
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label className="text-dark">Student ID</Form.Label>
            <Form.Control
              type="text"
              value={enrollmentData.studentId}
              onChange={(e) =>
                setEnrollmentData({ ...enrollmentData, studentId: e.target.value })
              }
              required
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label className="text-dark">Date of Birth</Form.Label>
            <Form.Control
              type="date"
              value={enrollmentData.dateOfBirth}
              onChange={(e) =>
                setEnrollmentData({ ...enrollmentData, dateOfBirth: e.target.value })
              }
              required
            />
          </Form.Group>
        </Form>
      </Modal>
    </div>
  );
};

export default StudentOnboardingPage;
